import React, {useState} from 'react';
import axios from 'axios';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
      minWidth: 200
    },
  },
}));
function Add() {
  const classes = useStyles();

  const [body, setBody] = useState({
    author: "",
    date_posted: "",
    title:"",
    content:"",
    topic: ""
  });
  function changeBodyInfo(e) {
     setBody({...body, [e.target.className]:e.target.value})
     console.log(body)
  }
  function onSubmit() {

  }
  return(
    <div className="Add">
    <form className={classes.root} noValidate autoComplete="off">
      <TextField label="Your name" type="text" name = "author" defaultValue={body.author} onChange = {changeBodyInfo}/><br></br>
      <TextField
        id="date"
        label="Birthday"
        type="date"
        defaultValue="2017-05-24"
        className={classes.textField}
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField label="Content" type="text" name = "content" defaultValue={body.content} onChange = {changeBodyInfo}/>
   </form>
       <Button className="submit" onClick = {onSubmit}><strong>Post</strong></Button>
    </div>
  )
}

export default Add;
